class Solution {
    public int largestValsFromLabels(int[] values, int[] labels, int numWanted, int useLimit) {
        //åˆ›å»ºä¼˜å…ˆé˜Ÿåˆ—ï¼Œé˜Ÿåˆ—ä»å¤§åˆ°å°æ’åº
        PriorityQueue<Information> pq = new PriorityQueue<>((x, y) -> y.value - x.value);
