class Solution:
    def smallestSufficientTeam(self, req_skills: List[str], people: List[List[str]]) -> List[int]:
        m = len(req_skills)
        n = len(people)

        skill_to_index = dict()
        for i, skill in enumerate(req_skills):
            skill_to_index[skill] = i

        person_mask = [0] * n
        for i, skills in enumerate(people):
            for skill in skills:
                person_mask[i] |= (1 << skill_to_index[skill])

        @cache
        def dfs(i: int, skill_mask: int) -> (bool, List[int]):
            if i == n:
                if skill_mask == (1 << m) - 1:
                    return True, []
                return False, []

            check1, lst1 = dfs(i + 1, skill_mask | person_mask[i])
            check2, lst2 = dfs(i + 1, skill_mask)

            if not check1 and not check2:
                return False, []

            if not check1:
                return True, lst2

            if not check2:
                return True, lst1 + [i]

            if len(lst1) + 1 <= len(lst2):
                return True, lst1 + [i]
            else:
                return True, lst2

        return dfs(0, 0)[1]
```
