class Solution {
    public List<Integer> minSubsequence(int[] nums) {
         //将数组排序
         Arrays.sort(nums);
         int size=nums.length;
         int cout;
         for(cout=size-1;cout>=0;cout--){
             int sum1=0;
             int sum2=0;
                for(int i=0;i<cout;i++){
                    sum1=sum1+nums[i];
                }
                for(int j=size-1;j>=cout;j--){
                    sum2=sum2+nums[j];
                }
                if(sum2>sum1){
                    break;
                }
         }
         List<Integer> list=new ArrayList<Integer>();
        for(int i=size-1;i>=cout;i--){
            list.add(nums[i]);
        }
        return list;
    }
}
