class Solution {
    public int maxOperations(int[] nums, int k) {
        Map<Integer, Integer> map = new HashMap<>(nums.length);
        //ç»Ÿè®¡æ¯ä¸ªæ•°æ®å‡ºç°çš„æ¬¡æ•°ï¼Œkeyä¸ºæ•°æ®ï¼Œvalueä¸ºæ¬¡æ•°
        for (int num : nums) {
            Integer i = map.getOrDefault(num, 0);
