        }
        int result = 0;
        for (int num : nums) {
            // æ±‚å’Œè¾¾åˆ°Kçš„æ•°æ®
            int x = k - num;
            // ä»mapè·å–x
            int i = map.get(num);
            //å¦‚æœæ¬¡æ•°å°äºç­‰äº0ï¼Œè¯´æ˜æ•°æ®è¢«ä½¿ç”¨è¿‡äº†ã€å°±ç®—åé¢éå†åˆ°ä»–ï¼Œä¹Ÿå¯ä»¥è·³è¿‡äº†ã€‘
            if (i <= 0) {
