typedef pair<int, int> PII;

class Solution {
public:
    int getNumberOfBacklogOrders(vector<vector<int>>& orders) {
        priority_queue<PII> A, B;
        int MOD = 1e9 + 7;
        long long ans = 0;
        
        //buy >= sell ‰∏§‰∏™ËÆ¢ÂçïÊâçËÉΩÂåπÈÖç
        for(auto &i : orders){
            if(i[2] == 0)   //buy Â§ßÊ†πÂ†Ü
                A.push({i[0], i[1]});
            else            //sell Â∞èÊ†πÂ†ÜÔºåÊääpriceÂèò‰∏∫Ë¥üÊï∞pushÂ∞±ÊòØÂ∞èÊ†πÂ†Ü
                B.push({-i[0], i[1]});
            // Êõ¥Êñ∞ÊÄªËÆ¢ÂçïÊï∞Èáè
            ans += i[1];
            //Ê∂àÈô§
            while(!A.empty()){
                // sellÂ§ß‰∫ébuy Êó†Ê≥ïÂåπÈÖç
                if(B.empty() || -B.top().first > A.top().first)
                    break;
                PII x = A.top();
                A.pop();
                PII y = B.top();
                B.pop();
                // ÂåπÈÖçÊàêÂäü
                int nu = min(x.second, y.second);
                x.second -= nu;
                y.second -= nu;
                ans -= 2 * nu;
                if(x.second)
                    A.push(x);
                else
                    B.push(y);
            }
            // cout << " " << ans << endl;
        }
        return ans % MOD;
    }
};
