class Solution {
    public String[] findRelativeRanks(int[] score) {
        String[] ans = new String[score.length];
        int[] pair = new int[2]; //一个数组对，（序号，分数）
        List<int[]> list = new ArrayList<>();
        for (int i = 0; i < score.length; i++){
            pair = new int[]{i, score[i]};
            list.add(pair);
        }
        Collections.sort(list, new Comparator<int[]>(){
            public int compare(int[] x, int[] y){
                return y[1]-x[1];//按照分数从大到小排序
            }
        });
        for (int i = 0; i < list.size(); i++){    
            int[] s = list.get(i);   //s[0]是该分数在score中的索引号
            if (i == 0) ans[s[0]] = "Gold Medal";
            if (i == 1) ans[s[0]] = "Silver Medal";
            if (i == 2) ans[s[0]] = "Bronze Medal";
            if (i > 2) ans[s[0]] = i+1 +"";
        }
        return ans;


    }
}
