```C++ []
class Solution {
public:
    constexpr static int dir_x[] = { 1, -1, 0, 0, 1, 1, -1, -1 };
    constexpr static int dir_y[] = { 0, 0, -1, 1, -1, 1, -1, 1 };
    int n, m;

    int flipChess(vector<string>& chessboard) {
        n = chessboard.size();
        m = chessboard[0].size();
        
        int res = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (chessboard[i][j] == '.') {
                    vector<string> copy{ chessboard.begin(), chessboard.end() };
                    copy[i][j] = 'X';
                    int cnt = process(copy, i, j);
                    res = std::max(res, cnt);
                }
            }
        }

        return res;
    }

    int process(vector<string>& board, int x, int y) {
        vector<int> q;

        for (int i = 0; i < 8; i++) {
            int new_x = x + dir_x[i];
            int new_y = y + dir_y[i];
            auto temp = search(board, new_x, new_y, dir_x[i], dir_y[i]);
            q.insert(q.end(), temp.begin(), temp.end());
        }

        int res = q.size();
        while (!q.empty()) {
            int pos = q.back();
            q.pop_back();
            res += process(board, pos / 10, pos % 10);
        }

        return res;
    }
    
    vector<int> search(vector<string>& board, int x, int y, int step_x, int step_y) {
        vector<int> temp;
        bool flag = false;

        while (check(x, y)) {
            if (board[x][y] != 'O') {
                flag = (board[x][y] == 'X');
                break;
            } else {
                temp.push_back(x * 10 + y);
                board[x][y] = 'X';
            }
            x += step_x;
            y += step_y;
        }
        if (!flag) {
            while (!temp.empty()) {
                int pos = temp.back();
                temp.pop_back();
                board[pos / 10][pos % 10] = 'O';
            }
        }

        return temp;
    }

    bool check(int x, int y) {
        return x >= 0 && x < n && y >= 0 & y < m;
    }
};
