func addTwoNumbers(l1 *ListNode, l2 *ListNode) *ListNode {
    var newHead = new(ListNode) //新链表头结点
    var p,q,r = l1,l2,newHead
    var c = 0                   //进位
    for p!=nil || q!=nil || c!=0{   //加数,被加数,进位任意不为0 运算继续
        var value = 0
        if p!=nil{
            value+=p.Val    //获取加数
            p=p.Next
        }
        if q!=nil{
            value+=q.Val    //获取被加数
            q=q.Next
        }
        if c!=0{
            value += c      //获取进位
        }
        r.Next = new(ListNode)
        r.Next.Val = value%10      
        c = value/10        //获得下一位进位
        r=r.Next
    }
    return newHead.Next 
}
